def predict(model, x):
    if isinstance(x, np.ndarray):
        x = torch.from_numpy(x.astype(np.float32)).to(device)

    with torch.no_grad():
        y_pred = model(x).argmax(axis=-1)

    return y_pred.cpu().numpy()

